import { useState } from 'react'



export default function Faq({ question, answer }) {
    const [isOpen, setIsOpen] = useState(false);
    return (
        <div className="border-b border-gray-300 w-full">
            <button
                className="flex justify-between items-center w-full py-4 text-left lg:text-lg font-semibold focus:outline-none"
                onClick={() => setIsOpen(!isOpen)}
            >
                {question}
                <img
                    src="/services/faq-arrow.png"
                    className={`h-5 object-contain transform transition-transform duration-300 ease-in-out ${isOpen ? "rotate-180" : "rotate-0"
                        }`}
                />{" "}
            </button>
            {isOpen && <div className="pb-4  text-gray-900">{answer}</div>}
        </div>
    )
}
